//on hover affect for the Header
export const HeaderHover = () => {
    //elements
    let headerContainer = document.querySelector('.header-container');
    // let menuLine = document.querySelector('.menu-line');
    // let link = document.querySelectorAll('.link');
    let headerContainerStyle = headerContainer.currentStyle || window.getComputedStyle(headerContainer);
    let mousePosition;

    //on mouseover and out event
    headerContainer.addEventListener(
        'mousemove',
        function (event) {
          event.preventDefault();

          mousePosition = {
            x: event.clientX + (parseInt(headerContainerStyle.width) ),
