import React from 'react';
import Nav from 'react-bootstrap/Nav';
import lang from '../lang/en.json';
import profile from '../assets/profile.jpeg';
import { useEffect } from 'react';

export default function Navbar() {
  useEffect(() => {
    let menuButton = document.querySelector('.menu-button');
    // let navbarLinks = document.querySelector('.link-bar');
    let linkTop = document.querySelectorAll('.link-top');
    let linkBottom = document.querySelectorAll('.link-bottom');

    menuButton.addEventListener("mouseover", function () {
        linkTop.forEach(element => {
            element.style.top = "0px";
        });
        linkBottom.forEach(element => {
            element.style.bottom = "0px";
        });
    });
    menuButton.addEventListener("mouseout", function () {
        linkTop.forEach(element => {
            element.style.top = "-26px";
        });
        linkBottom.forEach(element => {
            element.style.bottom = "-26px";
        });
    });
  }, []);

  return (
    <div className="nav-container">
      <div className="nav-logo">
        <img src={profile}></img>
        <a href="/">{lang.nebilsahin}</a>
      </div>
      <div className="nav-links">
        <Nav
          activeKey="/home"
          // onSelect={(selectedKey) => alert(`selected ${selectedKey}`)}
        >
          <Nav.Item class="menu-button">
            <div>Menu</div>
            <div class="menu-line"></div>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link className="link-top" href="/home">
              {lang.home}
            </Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link className="link-bottom" eventKey="link-1">
              {lang.skills}
            </Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link className="link-top" eventKey="link-2">
              {lang.projects}
            </Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link className="link-bottom" eventKey="link-2">
              {lang.about}
            </Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link className="link-top" eventKey="link-2">
              {lang.contact}
            </Nav.Link>
          </Nav.Item>
        </Nav>
      </div>
    </div>
  );
}
