import React from 'react';
import Nav from 'react-bootstrap/Nav';
import lang from '../lang/en.json';
import profile from '../assets/profile.jpeg';
import { useEffect } from 'react';

export default function Navbar() {
  //effects after component has been rendered
  useEffect(() => {
    //on hover affect for the menu
    //elements
    let menuButton = document.querySelector('.menu-button');
    let menuLine = document.querySelector('.menu-line');
    let link = document.querySelectorAll('.link');
    let linkTop = document.querySelectorAll('.link-top');
    let linkBottom = document.querySelectorAll('.link-bottom');

    //on mouseover and out event
    menuButton.addEventListener('mouseover', function () {
      menuLine.style.width = '80px';
      linkTop.forEach((element) => {
        element.style.top = '0px';
      });
      linkBottom.forEach((element) => {
        element.style.bottom = '0px';
      });
    });
    menuButton.addEventListener('mouseout', function () {
      menuLine.style.width = '20px';
      link.forEach((element) => {
        if(element.style.top){
          element.style.top = '-40px';
        }else if(element.style.bottom){
          element.style.bottom = '-40px';

        }
      });
    });
    link.forEach((element) => {
      element.addEventListener('mouseover', function () {
        menuLine.style.width = '80px';
        linkTop.forEach((element) => {
          element.style.top = '0px';
        });
        linkBottom.forEach((element) => {
          element.style.bottom = '0px';
        });
      });
      element.addEventListener('mouseout', function () {
        menuLine.style.width = '20px';
        linkTop.forEach((element) => {
          element.style.top = '-40px';
        });
        linkBottom.forEach((element) => {
          element.style.bottom = '-40px';
        });
      });
    });
  }, []);

  return (
    <div className="nav-container">
      <div className="nav-logo">
        <img src={profile}></img>
        <a href="/">{lang.nebilsahin}</a>
      </div>
      <div className="nav-links">
        <Nav
          activeKey="/home"
          // onSelect={(selectedKey) => alert(`selected ${selectedKey}`)}
        >
          <Nav.Item className="menu-button">
            <div>Menu</div>
            <div className="menu-line"></div>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link className="link link-top" href="/home">
              {lang.home}
            </Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link className="link link-bottom" eventKey="link-1">
              {lang.skills}
            </Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link className="link link-top" eventKey="link-2">
              {lang.projects}
            </Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link className="link link-bottom" eventKey="link-2">
              {lang.about}
            </Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link className="link link-top" eventKey="link-2">
              {lang.contact}
            </Nav.Link>
          </Nav.Item>
        </Nav>
      </div>
    </div>
  );
}
